import React from 'react'

class AddFishForm extends React.Component {
    nameRef = React.createRef();
    priceRef = React.createRef();
    statusRef = React.createRef();
    descRef = React.createRef();
    submitRef = React.createRef();
    createFish = (event) => {
        event.preventDefault();
        console.log(this.nameRef.current.value);

    }

    render() {
        return (
            <React.Fragment>
                <form className="fish-edit" onSubmit={this.createFish}>
                    <input name="name" ref={this.nameRef} type="text" placeholder="name"></input>
                    <input name="price" ref={this.priceRef} type="text" placeholder="price"></input>
                    <select name="status" ref={this.statusRef}>
                        <option value="available">Fresh!</option>
                        <option value="unavailable">Sold Out!</option>
                    </select>
                    <textarea name="desc" ref={this.descRef} placeholder="desc"></textarea>
                    <button type="submit" ref={this.submitRef}>+ Add fish</button>
                </form>
            </React.Fragment>
        )
    }
}

export default AddFishForm;
